INSERT INTO comics (name, description, image, source) SELECT 'Age of Ultron Companion ', 'As Ultron’s onslaught continues across the planet, Black Widow pays a high price in San Francisco and Captain Marvel joins Captain Britain in a fight to save London! The Fantastic Four return from a jaunt across time and space in a futile bid to save their homeworld! Otto Octavius, the Superior Spider-Man, believes it will be simple to vanquish an A.I. threat…but his vanity will prove his undoing! And in Los Angeles, Ultron’s “son” — the android Runaway Victor Mancha — battles to prove he’s nothing like his “father.” Ultron’s attack also batters the Fearless Defenders, Uncanny Avengers, Wolverine and more. And in the aftermath, witness a dramatic change for founding Avenger Hank Pym! Collecting AVENGERS ASSEMBLE #14AU-15AU, FANTASTIC FOUR (2013) #5AU, FEARLESS DEFENDERS #4AU, SUPERIOR SPIDER-MAN #6AU, ULTRON #1AU, UNCANNY AVENGERS #8AU, WOLVERINE & THE X-MEN #27AU and AGE OF ULTRON #10AI.', 'http://i.annihil.us/u/prod/marvel/i/mg/c/90/536cf5c5a2ee7.jpg', 'Marvel' WHERE NOT EXISTS (SELECT name FROM comics WHERE name = 'Age of Ultron Companion ');
